package basics;

public class Example_Continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=1;
		while (x<=10){
			System.out.println("Welcome");
			x++;
			
			if(x==5) {
				System.out.println("hai");
				continue;
				
			}
			System.out.println("Pavan");
		}

	}

}
